SELECT
	P.ProductName,
	SUM(S.QuantitySold) AS TotalSales 
	FROM Sales S
	JOIN Products P 
	ON S.ProductID = P.ProductID GROUP BY P.ProductName;


SELECT SP.Region,
	SUM(S.QuantitySold) AS TotalSales FROM Sales S
	JOIN Salespersons SP ON S.SalespersonID = SP.SalespersonID GROUP BY SP.Region;


SELECT SP.SalespersonName, 
	AVG(S.QuantitySold) AS AverageSales FROM Sales S
	JOIN Salespersons SP 
	ON S.SalespersonID = SP.SalespersonID GROUP BY SP.SalespersonName;

